#include <string>
#include <regex>

int main(int argc, char* argv[]) {
   
    std::string filePath = argv[1];
    std::string pattern;

    std::getline(std::cin, pattern);

    // Open the file
    std::ifstream file(filePath);
    if (!file.is_open()) {
        std::cerr << "Failed to open file: " << filePath << std::endl;
        return 1;
    }

    // Compile the regular expression
    std::regex transformRegex(pattern);
    std::string line;
    bool found = false;

    // Read the file line by line
    while (std::getline(file, line)) {
        // Search for the regex pattern in each line
        if (std::regex_search(line, transformRegex)) {
            found = true;
            break;
        }
    }

    // Close the file
    file.close();

    // Output the result
    if (found) {
        std::cout << "REGEXP PASS" << std::endl;
    } else {
        std::cout << "REGEXP FAILED" << std::endl;
    }

    return 0;
}

