package main

import (
	"bufio"
	"os"
	"strings"
)

func main() {
	scanner := bufio.NewScanner(os.Stdin)
	for scanner.Scan() {
		input := scanner.Text()
		output := strings.ToUpper(input)
		println(output)
	}
	if err := scanner.Err(); err != nil {
		println("error reading input:", err)
	}
}

